var user={};
var minajax=function(){

}
user.getInfoById=function(id,callback)
{
    this.minAjax({
        url:"http://api.bilibili.cn/userinfo",
        type:"GET",
        data:{
          mid:id
        },
        success:function(a){
            callback(JSON.parse(a));
        }
    })

}
user.init=function(a)
{
    this.minAjax=a;
}
module.exports=user;